class Cache {
  constructor() {
    this.data = {};
  }

  set(name, value) {
    const keys = Object.keys(this.data);
    // 防止无限制增长，长度大于100，删除第一个
    if (keys.length >= 100) {
      var firstKey = keys.shift();
      delete this.data[firstKey];
    }
    this.data[name] = value;
  }

  get(name) {
    if (name) {
      return this.data[name] || null;
    }
    return null;
  }
}

module.exports = Cache;